INSERT INTO `y_news` (`id`, `content`, `source`) VALUES
(1419, '<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">园区各企业：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 28pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">管理必须和文化相结合，才能受到实效。中国式管理模式如何真正提高企业家的领导力，如何做到在竞争激烈的情况下应对挑战。园区与宁波市营销管理职业培训学校联合举办《中国式管理模式总裁班》，特邀请中国式魔鬼训练第一人张家铭老师到宁波来演讲。现将有关事项通知如下：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''">&nbsp;一、参加对象</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">：企业总</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">经理</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">以上高层管理人员。</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">&nbsp;二、时间和地点</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 23.5pt; MARGIN-BOTTOM: 0pt; BACKGROUND: rgb(255,255,255)" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">&nbsp;&nbsp;时间：2012年12月20日（周四）9：00—17：00（一天）</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 23.5pt; MARGIN-BOTTOM: 0pt; BACKGROUND: rgb(255,255,255)" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">&nbsp;&nbsp;地点：宁波金港大酒店文荟阁（扬善路51号）</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; BACKGROUND: rgb(255,255,255)" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">三、费用：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">1980元，</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">软件园企业<SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">免</SPAN>费参加</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">。</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; BACKGROUND: rgb(255,255,255)" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">四、主要内容和主讲人</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 23.5pt; MARGIN-BOTTOM: 0pt; BACKGROUND: rgb(255,255,255)" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)">1.内容：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; BACKGROUND: rgb(255,255,255); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''; mso-shading: rgb(255,255,255)"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 28pt; MARGIN-BOTTOM: 0pt; MARGIN-RIGHT: -145.7pt" class=p15><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">中国式领导的自我修炼&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中华文化和中国式团队文化的结合&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; MARGIN-RIGHT: -145.7pt" class=p15><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">&nbsp;&nbsp;&nbsp;“儒释道”对领导力的提升&nbsp;&nbsp;&nbsp;企业如何选人、育人、用人、留人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; MARGIN-RIGHT: -145.7pt" class=p15><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">&nbsp;&nbsp;&nbsp;&nbsp;中国式团队打造的核心秘诀&nbsp;&nbsp;如何制定公司的战略和规划</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; MARGIN-RIGHT: -145.7pt" class=p15><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">&nbsp;&nbsp;&nbsp;&nbsp;策略行销的秘诀&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如何塑造具有亮剑精神的营销团队</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; MARGIN-RIGHT: -145.7pt" class=p15><SPAN style="FONT-FAMILY: ''宋体''; FONT-SIZE: 12pt; mso-spacerun: ''yes''">&nbsp;</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">&nbsp;&nbsp;&nbsp;如何打造学习型的团队&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;顾问咨询会（一对一咨询服务）</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 27.75pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(51,51,51); FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''">2.主讲人：张家铭</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(51,51,51); FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 27.75pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(51,51,51); FONT-SIZE: 14pt; mso-spacerun: ''yes''">中国式魔鬼训练第一人；</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">中国最具实战的营销训练师；卓越团队建设专家、企业高级顾问；</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">天智教育训练集团——华东区总经理</SPAN></P><SPAN style="FONT-FAMILY: ''楷体_GB2312''; COLOR: rgb(0,0,0); FONT-SIZE: 14pt; mso-spacerun: ''yes''">\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; FONT-WEIGHT: bold; mso-spacerun: ''yes''">五、报名要求</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">：</SPAN><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''"><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 28pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''">名额有限，即日起额满为止，请各单位和行业协会接到通知后积极报名参加。</SPAN></P>\r\n<P style="LINE-HEIGHT: 30pt; MARGIN-TOP: 0pt; TEXT-INDENT: 28pt; MARGIN-BOTTOM: 0pt" class=p0><SPAN style="FONT-FAMILY: ''楷体_GB2312''; FONT-SIZE: 14pt; mso-spacerun: ''yes''"><IMG align=absMiddle src="/hcms/sEdit/images/common.gif"><A href="/uploadfile/attahces/201212171355729520.rar" target=_blank>中国式管理模式总裁班的通知.rar</A></SPAN></SPAN></P>', '宁波产业园区'),
